import React from "react";

const Congrats = (props) => {
  const startQuiz = (e) => {
    e.preventDefault();
    props.setPageNumber(1); // Move back to the first question
  };

  return (
    props.isShowing &&
    props.quizData && ( // Add check for quizData
      <div className="modal-content">
        <h2>congrats!</h2>
        <p>
          You answered {props.answeredCorrectly / props.quizData.length - 1}% of
          questions correctly
        </p>
        <button type="submit" className="close-btn" onClick={startQuiz}>
          Start
        </button>
      </div>
    )
  );
};

export default Congrats;
