import React from "react";

const Congrats = (props) => {
  const { quizData, answeredCorrectly } = props;

  // Calculate the percentage of correct answers
  const percentageCorrect =
    quizData.length === 0 ? 0 : (answeredCorrectly / quizData.length) * 100;

  const startQuiz = (e) => {
    e.preventDefault();
    props.setPageNumber(1); // Go back to the start page to restart the quiz
  };

  return (
    props.isShowing && (
      <div className="modal-content">
        <h2>Congrats!</h2>
        <p>
          You answered {percentageCorrect.toFixed(2)}% of questions correctly
        </p>
        <button type="submit" className="close-btn" onClick={startQuiz}>
          Play Again
        </button>
      </div>
    )
  );
};

export default Congrats;
