  const [pageNumber,setPageNumber]=useState(1)
  return (
    <div className="App">
      <Question isShowing={pageNumber===1} setPageNumber={setPageNumber}/>
      <Quiz isShowing={pageNumber===2} setPageNumber={setPageNumber}/>
      <Congrats isShowing={pageNumber===3} setPageNumber={setPageNumber}/>
    </div>
  );
}

