import React from "react";
import ReactDOM from "react-dom";
import { useState, useEffect } from "react";
import axios from "axios";
import "./quiz.css";

const Quiz = (props) => {
  const startQuiz = (e) => {
    e.preventDefault();
    console.log(e);

    //this will help us from not reloading page again and again

    props.setPageNumber(3);
  };
  return (
    props.isShowing && (
      <main>
        <div className="modal-container">
          <div className="modal-content">
            {/* <h2>congrats!</h2>
            <p>You answered 0% of questions correctly</p>
            <button className="close-btn">play again</button> */}
          </div>
        </div>
        <section className="quiz">
          <p className="correct-answers">correct answers : 0/0</p>
          <article className="container">
            <h2>Which of the following sports is not part of the triathlon?</h2>
            <div className="btn-container">
              <button className="answer-btn">Cycling</button>
              <button className="answer-btn">Swimming</button>
              <button className="answer-btn">Running</button>
              <button className="answer-btn">Horse-Riding</button>
            </div>
          </article>
          {/* <button className="next-question">next question</button> */}
          <button
            type="submit"
            className="next-question"
            onClick={(e) => startQuiz(e)}
          ></button>
        </section>
      </main>
    )
  );
};

export default Quiz;
