import React from "react";
import "./question.css";
import { useState } from "react";

const Question = () => {
  const [quizStarted, isquizStarted] = useState("false");
  const [inputValue, setinputValue] = useState("");
  const [category, setCategory] = useState("");
  const [difficulty, setDifficulty] = useState("");

  const startQuiz = (e) => {
    e.preventDefault();
    //this will help us from not reloading page again and again
    isquizStarted(true);
  };

  function handleInputChange(e) {
    setinputValue(e.target.value);
  }

  function handleCategoryChange(value) {
    console.log(value);

    setCategory(value);
  }

  function handleDifficultyChange(e) {
    setDifficulty(e.target.value);
  }
  console.log({ category });
  return (
    <div className="App">
      <main className="style">
        <section className="quiz quiz-small">
          <form className="setup-form">
            <h2>Setup Quiz</h2>
            <div className="form-control">
              <label>Number of Questions</label>
              <input
                type="number"
                name="amount"
                id="amount"
                onChange={handleInputChange}
                className="form-input"
                min="1"
                max="50"
                value={inputValue}
              />
            </div>
            <div className="form-control">
              <label>Category</label>
              <select name="category" id="category" className="form-input">
                <option
                  value={category}
                  onChange={() => handleCategoryChange("Sports")}
                >
                  Sports
                </option>
                <option
                  value={category}
                  onChange={() => handleCategoryChange("History")}
                >
                  History
                </option>
                <option
                  value={category}
                  onChange={() => handleCategoryChange("Politics")}
                >
                  Politics
                </option>
              </select>
            </div>
            <div className="form-control">
              <label>Select Difficulty</label>
              <select name="category" id="category" className="form-input">
                <option value={difficulty} onChange={handleDifficultyChange}>
                  Easy
                </option>
                <option value={difficulty} onChange={handleDifficultyChange}>
                  Medium
                </option>
                <option value={difficulty} onChange={handleDifficultyChange}>
                  Hard
                </option>
              </select>
            </div>
            <button
              type="submit"
              className="submit-btn"
              onClick={() => startQuiz()}
            >
              Start
            </button>
          </form>
        </section>
      </main>
    </div>
  );
};

export default Question;
