import React from "react";
import ReactDOM from "react-dom";
import { useState, useEffect } from "react";
import axios from "axios";
import "./quiz.css";

const Quiz = (props) => {
  const [questions, setQuestions] = useState([]);
  const [options, setOptions] = useState([]);
  const [quizStarted, setQuizStarted] = useState(false);
  const [data, setData] = useState(null);

  const startQuiz = async (e) => {
    e.preventDefault();
    console.log(e);
    setQuizStarted(true);

    try {
      const response = await axios.get(
        "https://opentdb.com/api.php?amount=10&category=21&difficulty=easy&type=multiple"
      );
      setData(response.data.results);
      console.log(data);
      const quizQuestions = data.map((item) => item.question);
      const allOptions = data.map((item) => [
        item.correct_answer,
        ...item.incorrect_answers,
      ]);

      setQuestions(quizQuestions);
      setOptions(allOptions);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
    props.setPageNumber(3);
  };

  //this will help us from not reloading page again and again

  return (
    props.isShowing &&
    quizData && (
      <main>
        <div className="modal-container">
          <div className="modal-content">
            {/* <h2>congrats!</h2>
            <p>You answered 0% of questions correctly</p>
            <button className="close-btn">play again</button> */}
          </div>
        </div>
        <section className="quiz">
          <p className="correct-answers">correct answers : 0/0</p>
          <article className="container">
            <h2>{data.questions}</h2>
            <div className="btn-container">
              <button className="answer-btn">{data.correct_answer}</button>
              <button className="answer-btn">
                {data.incorrect_answers[0]}
              </button>
              <button className="answer-btn">
                {data.incorrect_answers[1]}
              </button>
              <button className="answer-btn">
                {data.incorrect_answers[2]}
              </button>
            </div>
          </article>
          {/* <button className="next-question">next question</button> */}
          <button
            type="submit"
            className="next-question"
            onClick={(e) => startQuiz(e)}
          >
            next question
          </button>
        </section>
      </main>
    )
  );
};

export default Quiz;
