import React from "react";
import "./quiz.css";
import { useState } from "react";
import { Oval } from "react-loader-spinner";

const Quiz = (props) => {
  const { quizData } = props;
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  // const [answeredCorrectly, setAnsweredCorrectly] = useState(0);
  const [quizCompleted, setQuizCompleted] = useState(false);
  const [shuffledOptions, setShuffledOptions] = useState([]);

  const startQuiz = (e) => {
    e.preventDefault();
    // props.setLoading();
    // props.setPageNumber(3);
    if (currentQuestionIndex < quizData.length) {
      props.setAnsweredCorrectly((prev) => prev); // Treat the question as incorrect
    }

    // Move to the next question
    setCurrentQuestionIndex((prev) => prev + 1);

    // If the user has completed all questions, show the "Congrats" page
    if (currentQuestionIndex === quizData.length - 1) {
      setQuizCompleted(true);
      props.setPageNumber(3);
    }
  };

  const shuffleOptions = (options) => {
    // Randomly shuffle the array of options
    const shuffled = options.sort(() => Math.random() - 0.5);
    setShuffledOptions(shuffled);
  };

  const handleAnswerClick = (answer) => {
    // Check if the selected answer is correct
    // if (currentQuestionIndex === quizData.length) {
    //   props.setPageNumber(1);
    //   setQuizCompleted(true);
    //   return;
    // }
    const correctAnswer = quizData[currentQuestionIndex].correct_answer;

    const userAnsweredCorrectly = answer === correctAnswer;

    if (currentQuestionIndex < quizData.length && !userAnsweredCorrectly) {
      props.setAnsweredCorrectly((prev) => prev);
    } else if (userAnsweredCorrectly) {
      props.setAnsweredCorrectly((prev) => prev + 1);
    }

    // Move to the next question
    setCurrentQuestionIndex((prev) => prev + 1);
  };

  quizData && console.log(currentQuestionIndex, quizData.length);
  return props.loading ? (
    <div className="loader-container">
      <Oval
        height={80}
        width={80}
        color="#0000FF"
        visible={true}
        ariaLabel="oval-loading"
        secondaryColor="#4fa94d"
        strokeWidth={2}
        strokeWidthSecondary={2}
      />
    </div>
  ) : (
    props.isShowing &&
      !quizCompleted &&
      quizData &&
      currentQuestionIndex < quizData.length &&
      props.loading === false && (
        <main>
          <div className="modal-container">
            <div className="modal-content"></div>
          </div>
          <section className="quiz">
            <p className="correct-answers">
              correct answers : {props.answeredCorrectly}/{currentQuestionIndex}
            </p>
            <article className="container">
              <h2
                dangerouslySetInnerHTML={{
                  __html: quizData[currentQuestionIndex].question,
                }}
              ></h2>
              <div className="btn-container">
                {shuffledOptions.map((answer, index) => (
                  <button
                    className="answer-btn"
                    onClick={() =>
                      handleAnswerClick(
                        quizData[currentQuestionIndex].correct_answer
                      )
                    }
                  >
                    {quizData[currentQuestionIndex].correct_answer}
                  </button>
                ))}
                {quizData[currentQuestionIndex].incorrect_answers.map(
                  (answer, index) => (
                    <button
                      key={index}
                      className="answer-btn"
                      onClick={() => handleAnswerClick(answer)}
                    >
                      {answer}
                    </button>
                  )
                )}
              </div>
            </article>
            {currentQuestionIndex !== quizData.length && (
              <button
                type="submit"
                className="next-question"
                onClick={(e) => startQuiz(e)}
              >
                next question
              </button>
            )}
          </section>
        </main>
      )
  );
};

export default Quiz;
