import './App.css';
import Question from './Components/Question';
import Quiz from './Components/Quiz';
import Congrats from './Components/Congrats';
import { useState } from 'react';

function App() {
  const [pageNumber,setPageNumber]=useState(1)
  const [quizData, setQuizData]= useState(null);
  const [loading, setLoading] = useState(false);
  const [answeredCorrectly, setAnsweredCorrectly] = useState(0);


  console.log({loading});
  return (
    <div className="App">
      {pageNumber === 1 && (
      <Question loading={loading} setLoading={setLoading} setQuizData={setQuizData} isShowing={pageNumber===1} setPageNumber={setPageNumber}/
      >
    )}
      {quizData && pageNumber === 2( 
      <Quiz loading={loading} quizData={quizData} isShowing={pageNumber===2} setPageNumber={setPageNumber} answeredCorrectly={answeredCorrectly} setAnsweredCorrectly={setAnsweredCorrectly} 
      />
      )}
      <Congrats answeredCorrectly={answeredCorrectly} setAnsweredCorrectly={setAnsweredCorrectly}isShowing={pageNumber===3} setPageNumber={setPageNumber} quizData={quizData}/>
    </div>
  );
}

export default App;
