import React from "react";
import ReactDOM from "react-dom";
import { useState, useEffect } from "react";
import axios from "axios";
import "./quiz.css";

const Quiz = (props) => {
  const [questions, setQuestions] = useState([]);
  const [options, setOptions] = useState([]);
  const [quizStarted, setQuizStarted] = useState(false);
  const { quizData } = props;
  const startQuiz = async (e) => {
    e.preventDefault();

    props.setPageNumber(3);
  };

  //this will help us from not reloading page again and again

  return (
    props.isShowing &&
    quizData && (
      <main>
        <div className="modal-container">
          <div className="modal-content">
            {/* <h2>congrats!</h2>
            <p>You answered 0% of questions correctly</p>
            <button className="close-btn">play again</button> */}
          </div>
        </div>
        <section className="quiz">
          <p className="correct-answers">correct answers : 0/0</p>
          <article className="container">
            <h2>{quizData.questions}</h2>
            <div className="btn-container">
              <button className="answer-btn">{quizData.correct_answer}</button>
              <button className="answer-btn">
                {quizData.incorrect_answers[0]}
              </button>
              <button className="answer-btn">
                {quizData.incorrect_answers[1]}
              </button>
              <button className="answer-btn">
                {quizData.incorrect_answers[2]}
              </button>
            </div>
          </article>
          {/* <button className="next-question">next question</button> */}
          <button
            type="submit"
            className="next-question"
            onClick={(e) => startQuiz(e)}
          >
            next question
          </button>
        </section>
      </main>
    )
  );
};

export default Quiz;
