import React from "react";
import ReactDOM from "react-dom";
import { useState, useEffect } from "react";
import axios from "axios";
import "./quiz.css";

let option1;
let option2;
let option3;
let option4;

let questions;
const Quiz = (props) => {
  const startQuiz = (e) => {
    e.preventDefault();
    console.log(e);

    //this will help us from not reloading page again and again

    props.setPageNumber(3);
  };
  return (
    props.isShowing && (
      <main>
        <div className="modal-container">
          <div className="modal-content">
            {/* <h2>congrats!</h2>
            <p>You answered 0% of questions correctly</p>
            <button className="close-btn">play again</button> */}
          </div>
        </div>
        <section className="quiz">
          <p className="correct-answers">correct answers : 0/0</p>
          <article className="container">
            <h2>`${questions}`</h2>
            <div className="btn-container">
              <button className="answer-btn">`${options1}`</button>
              <button className="answer-btn">`${options2}`</button>
              <button className="answer-btn">`${options3}`</button>
              <button className="answer-btn">`${options4}`</button>
            </div>
          </article>
          {/* <button className="next-question">next question</button> */}
          <button
            type="submit"
            className="next-question"
            onClick={(e) => startQuiz(e)}
          >
            next question
          </button>
        </section>
      </main>
    )
  );
};

export default Quiz;
