import React from "react";
import "./question.css";
import { useState } from "react";

const Question = () => {
  const [quizStarted, isquizStarted] = useState("false");
  const [inputValue, setinputValue] = useState("");
  const [category, setcategory] = useState("");
  const [Difficulty, setdifficulty] = useState("");

  const startQuiz = (e) => {
    e.preventQuiz();
    isquizStarted(true);
  };

  function handleInputChange(e) {
    setinputValue(e.target.value);
  }

  return (
    <div className="App">
      <main className="style">
        <section className="quiz quiz-small">
          <form className="setup-form">
            <h2>Setup Quiz</h2>
            <div className="form-control">
              <label>Number of Questions</label>
              <input
                type="number"
                name="amount"
                id="amount"
                onChange={handleInputChange}
                className="form-input"
                min="1"
                max="50"
                value={inputValue}
              />
            </div>
            <div className="form-control">
              <label>Category</label>
              <select name="category" id="category" className="form-input">
                <option value="sports">Sports</option>
                <option value="sports">History</option>
                <option value="sports">Politics</option>
              </select>
            </div>
            <div className="form-control">
              <label>Select Difficulty</label>
              <select name="category" id="category" className="form-input">
                <option value="easy">Easy</option>
                <option value="medium">Medium</option>
                <option value="hard">Hard</option>
              </select>
            </div>
            <button type="submit" className="submit-btn" onClick={startQuiz}>
              Start
            </button>
          </form>
        </section>
      </main>
    </div>
  );
};

export default Question;
