import React from "react";
import ReactDOM from "react-dom";
import { useState, useEffect } from "react";
import axios from "axios";
import "./quiz.css";

const Quiz = () => {
  return (
    <main>
      <div class="modal-container">
        <div class="modal-content">
          {/* <h2>congrats!</h2>
            <p>You answered 0% of questions correctly</p>
            <button class="close-btn">play again</button> */}
        </div>
      </div>
      <section class="quiz">
        <p class="correct-answers">correct answers : 0/0</p>
        <article class="container">
          <h2>Which of the following sports is not part of the triathlon?</h2>
          <div class="btn-container">
            <button class="answer-btn">Cycling</button>
            <button class="answer-btn">Swimming</button>
            <button class="answer-btn">Running</button>
            <button class="answer-btn">Horse-Riding</button>
          </div>
        </article>
        <button class="next-question">next question</button>
      </section>
    </main>
  );
};

export default Quiz;
